using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.AI;  public class MonsterMove : MonoBehaviour {     NavMeshAgent agent;     public Transform target;     public Vector3 currentPlayerPos;      private void Awake()     {         agent = GetComponent<NavMeshAgent>();     }      private void Start()     {         currentPlayerPos = target.position;         agent.SetDestination(currentPlayerPos);     }       // Update is called once per frame     void Update()     {         if (currentPlayerPos != target.position)         {             currentPlayerPos = target.position;             agent.SetDestination(currentPlayerPos);          }       } } 